<?php/*Template Name: gallery*/?><?php get_header(); ?><div class="two-collumns-single left">	<div class="gallery">	<?php head_title('GALLERY'); ?>	<?php 		$query_images_args = array('post_type' => 'attachment', 'post_mime_type' =>'image', 'post_status' => 'inherit', 'posts_per_page' => -1,);		$query_images = new WP_Query( $query_images_args );		foreach ( $query_images->posts as $image) {			echo '<a href="'.wp_get_attachment_url($image->ID,'large').'">';			echo wp_get_attachment_image($image->ID,'thumbnail');			echo '</a>';		}	?>	</div></div><?php get_sidebar(); ?><?php get_footer(); ?>